import React from 'react';

export default class Condamnation extends React.Component{

    async getPrisoners(){
        await fetch('http://localhost:8080/prisoners/read', {method: 'GET'}) // Envoi requete au serveur
              .then(res => res.json()) // Reponse de la requete
              .then((out) => { // Données a recuperer
                    return out.result;
              });
    }

    compare = (a,b) => {
        const nameA = a.lastname.toUpperCase();
        const nameB = b.lastname.toUpperCase();

        let comparison = 0;
        if (nameA > nameB) {
            comparison = 1;
        } else if (nameA < nameB) {
            comparison = -1;
        }
        return comparison;
    }

    createSelectItems = (array) => {
        let options = [];
        for(let i=0; i<array.length; i++){
            options.push(<option key={i} value={array[i].lastname + array[i].firstname}>{array[i].lastname + array[i].firstname}</option>)
        }
        return options;
    }

    render(){
        let prisoners = this.getPrisoners();
        return(
            <div>
                <div>
                    <p>Date decision : </p>
                    <input id="date_decision" type="text" placeholder="dd/mm/aaaa"></input>
                </div>
                <div>
                    <p>Choisir prisonier :</p>
                    <select>
                        {this.createSelectItems(prisoners.sort(this.compare))}
                    </select>
                </div>
                <div>
                    <p>Duree emprisonnement : </p>
                    <input id="duree_decision" type="text"></input>
                </div>
            </div>
        );
    }
}
